SELECT DISTINCT C.MSISDN, H.MULTISIM, H.FECHA, H.IDTRANS, H.IMPORTE, A.NAME, P.NAME PRODUCTO_NAME, H.MARKET, P.FLAG_SUSCRIPCION FROM (SELECT * FROM ( ALL_PARTITIONS )) H INNER JOIN TER_WRITE.TB_CLIENTES C ON C.IDCLI = H.IDCLI INNER JOIN TER_WRITE.TB_PRODUCTOS P ON P.IDPROD = H.IDPROD INNER JOIN TER_WRITE.TB_PARTNERS T ON P.IDPART = T.IDPART INNER JOIN TER_WRITE.TB_AGRUPADORES A	ON T.IDAGR = A.IDAGR AND C.MSISDN = :MSDIN AND H.FECHA >= :fecha1 AND H.FECHA <= :fecha2 ORDER BY  H.FECHA DESC